import React from 'react';
import { TextField } from '@material-ui/core';
import usetodoInputstate from './usetodoInputstate';

const Todotext = ({saveTodo}) => {

const {value,onChange,reset} = usetodoInputstate("");
    return (
        <form onSubmit={(event) => {
            event.preventDefault();
            saveTodo(value);
            reset();
          }}>
            <TextField variant="outlined" placeholder="Add todo" margin="normal" value={value} onChange={onChange} />
        </form>

    )
}

export default Todotext;
