import React, { useState } from 'react';
import { TextField } from '@material-ui/core';

const Search = (todo) => {

const [todoValue,setListValue] = useState("");

const setTodoValue = (e) => {
    setListValue(e.target.value);
}

const resetField = () => {
    setListValue("")
}

const addTodoList = (e) => {
    e.preventDefault();
    todo(todoValue);
    resetField();
}

    return (
        <form onSubmit={addTodoList}>
        <TextField variant="outlined" placeholder="Add todo" margin="normal" value={todoValue} onChange={setTodoValue} />
      </form>

    )
}

export default Search;
