import React, { useState } from 'react';


const Search = (props) => {

const [todoValue,setListValue] = useState("");

const setTodoValue = (e) => {
    setListValue(e.target.value);
}

const resetField = () => {
    setListValue("")
}

const addTodoList = (e) => {
    e.preventDefault();
    props.search(todoValue);
    resetField();
}

    return (
        <div className="Search">
            <input type = "text" className="search" value={todoValue} onChange={setTodoValue}/>
            <input type="submit" value="Add" onClick={addTodoList}/>

        </div>
    )
}

export default Search;
