import React, { useState } from 'react';
import { TextField } from '@material-ui/core';

const Search = (props) => {

const [todoValue,setListValue] = useState("");

const setTodoValue = (e) => {
    setListValue(e.target.value);
}

const resetField = () => {
    setListValue("")
}

const addTodoList = (e) => {
    e.preventDefault();
    props.search(todoValue);
    resetField();
}

    return (
        <form name="todo">
             <TextField label="Todo" variant="Todo" />
</form>
